import React, { Component } from 'react';
import { StyleSheet, View, Text, ScrollView, TouchableOpacity } from 'react-native';
import {connect} from 'react-redux';
import { fetchTodos } from '../../actions/todosActions';

class ShowTodos extends Component {

  // state={
  //   review:[]
  // }

    componentDidMount(){
      this.props.fetchTodos()
      // setInterval(
      //   () => this.props.fetchTodos(),
      //   1000
      // );
      }
    // componentDidUpdate(prevProps){
    //   const {reviews} = this.props;
    //   // this.props.fetchReviews();
    //   console.log("kuch ni")
    //   if(reviews !== prevProps.reviews){
    //     console.log("kuch toh hai")
    //     console.log(reviews)
    //     this.setState({review:reviews})
    //   }
    // }
    del = (_id) => {
      console.log('Pressed: '+_id)
    }

    render(){

        const todosItems = this.props.todos.map(({ _id, todo }) => (
            <View style={styles.todo} key={_id}>
                    <Text style={styles.todoTodo}>{todo}</Text>
                    <TouchableOpacity><Text style={styles.del} onPress={this.del(todo)}>X</Text>
                    </TouchableOpacity>                    
                    {/* <Text style={styles.reviewName}>-By {name}</Text> */}
            </View>
          )
            
        );

        return (
          // <View>
            <ScrollView>
              <View style={styles.todos}>
                  <Text style={styles.todosdiscp}>
                      Todo List
                  </Text>
                  {/* {reviewsItems} */}
                  {this.props.todosLoading?<View><Text>Loading...</Text></View>:todosItems}
                  {/* <View style={styles.review}>
                      <Text style={styles.reviewReview}>Review</Text>
                      <Text style={styles.reviewName}>-By name</Text>
                  </View>
                  <View style={styles.review}>
                      <Text style={styles.reviewReview}>Review</Text>
                      <Text style={styles.reviewName}>-By name</Text>
                  </View> */}
              </View>
            </ScrollView>
            // </View>
        );
    }

}

const styles = StyleSheet.create({
  todos: {
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    // backgroundColor: "#eee",
    marginTop: 15
  },
  todosdiscp: {
    // width: '70%',
    padding: 5,
    fontSize: 22,
    fontWeight: 'bold',
    marginBottom: 5
  },
  todo: {
    width: 300,
    padding: 8,
    // fontSize: 18,
    borderWidth: 1.5,
    borderColor: "#ddd",
    backgroundColor: '#eee',
    borderRadius: 10,
    marginBottom: 10,
    flexDirection:'row'
  },
  todoTodo: {
    fontSize: 18,
    color: '#3D3C3A',
    // textAlign: 'center'
  },
  del: {
    padding: 8,
    position:'absolute',
    right: 4,
    // textAlign: 'right',
    color:'red',
    fontSize: 18,
    fontWeight: 'bold'
  }
  // todoName: {
  //   fontSize: 15,
  //   color: '#6D6968'
  // }
});

const mapStateToProps=state=>({
    todos:state.todos.items,
    todosLoading:state.todos.todosLoading
});

export default connect(mapStateToProps, { fetchTodos })(ShowTodos);
// export default ShowReviews;
