// Sign up
export const register = (user) => (dispatch) => {
    axios({
        method: 'post',
        url: '/api/register',
        data: user
      }).then(user =>dispatch({
        type:REGESTER_SUCCESS,
        payload:user.data
        // console.log(user)
      })).catch(err => {
        console.log(err.response.status)
        dispatch(returnErrors(err.response.data, err.response.status,'REGESTER_FAIL'));
        dispatch({
          type:REGESTER_FAIL
      })})
}

//Log out

export const logout = () => {
    return {
        type:LOGOUT_SUCCESS
    }
}

//Login
export const login = (user) => (dispatch) => {
    axios({
        method:'post',
        url:'/api/auth',
        data: user
    }).then(user => {
        dispatch({
            type: LOGIN_SUCCESS,
            payload:user.data
        })
    }).catch(err => {
        console.log(err.response.status)
        dispatch(returnErrors(err.response.data, err.response.status,'LOGIN_FAIL'));
        dispatch({
            type:LOGIN_FAIL
        })
    })
}
