import { FETCH_TODOS, TODOS_LOADING, TODOS_LOADED } from '../actions/types';

const initialState={
    items:[],
    todosLoading: false
}
export default function(state=initialState,action){
    switch(action.type){
        case TODOS_LOADING:
        return{
            ...state,
            todosLoading:true
            }
        case TODOS_LOADED:
        return{
            ...state,
            todosLoading:false
            }
        case FETCH_TODOS:
        // localStorage.removeItem('price')
        return {
            ...state,
            items:action.payload
        };
        default:
        return state;
    };
}
